function getdata($member,$data){

global $array;

    foreach($array as $player => $info)
    {
        if($player == $member)
        {foreach($info as $infovalue => $dataname)
         {
         	if($infovalue == $data){
         	foreach($dataname as $datavalue => $value)
         	{
            echo '<p style="line-height:0px;">'.$datavalue.' => '.$value.'</p></br>';
         	}
        }
    }
	}
}
};

function getsocial($member,$data,$social){

global $array;

    foreach($array as $player => $info)
    {
        if($player == $member)
        {foreach($info as $infovalue => $dataname)
         {
         	if($infovalue == $data){
         	foreach($dataname as $datavalue => $value)
         	{
         		if($datavalue == $social){
            		return $value;
         	}
         	}
        }
    }
	}
}
};

function getsocials($member,$data){

    global $array;
    
        foreach($array as $player => $info)
        {
            if($player == $member)
            {foreach($info as $infovalue => $dataname)
             {
                 if($infovalue == $data){
                 foreach($dataname as $datavalue => $value)
                 {
                        if($datavalue=="twitter")
                        {
                        echo '<a href="'.$value.'"><i class="fab fa-twitter fa-lg icon-link"></i></a>';
                        }
                        if($datavalue=="youtube")
                        {
                        echo '<a href="'.$value.'"><i class="fab fa-youtube fa-lg icon-link"></i></a>';
                        }
                        if($datavalue=="facebook")
                        {
                        echo '<a href="'.$value.'"><i class="fab fa-facebook fa-lg icon-link"></i></a>';
                        }
                        if($datavalue=="instagram")
                        {
                        echo '<a href="'.$value.'"><i class="fab fa-instagram fa-lg icon-link"></i></a>';
                        }
                        if($datavalue=="twitch")
                        {
                        echo '<a href="'.$value.'"><i class="fab fa-twitch fa-lg icon-link"></i></a>';
                        }
                        if($datavalue=="web")
                        {
                        echo '<a href="'.$value.'"><i class="fab fab fa-safari fa-lg icon-link"></i></a>';
                        }
                    }
                 }
                 }
            }
        }
        };

function getinfo($member,$select){
global $array;

    foreach($array as $player => $info)
    {
        if($player == $member)
        {foreach($info as $infovalue => $dataname)
         {
         	if($infovalue==$select)
         	{
         	 return $dataname;	
         	}
		 }
        }
    }
}

